local function getCurrentFloor()
    local yPos = hrp.Position.Y
    if yPos >= -8 and yPos <= -1 then
        return floor1, "floor1"
    elseif yPos >= 8 and yPos <= 15 then
        return floor2, "floor2"
    elseif yPos >= 16 then
        return nil, "floor3"
    else
        return floor1, "floor1"
    end
end

